import os, imghdrfrom flask import Flask, render_template, requestfrom src.main import mainapp = Flask(__name__)APP_ROOT = os.path.dirname(os.path.abspath(__file__))@app.route('/')def index():    return render_template('upload.html')@app.route('/', methods=['POST'])def upload():    data_root = os.path.join(APP_ROOT, 'static/images')    # The argument of getlist is the input tag's name attribute    image = request.files.getlist('input')[0]    # get file name of input tag's object    filename = image.filename    inpath = os.path.join(data_root, 'input', filename)    # save input tag's object temorary    image.save(inpath)    # input the saved object to the main fucntion and get the output    main('demo', data_root, filename)    outpath = os.path.join('images', 'output',                filename.split('.')[0] + '_out.' + filename.split('.')[1])    return render_template('upload.html',             inpath=os.path.join('images', 'input', filename),            # predict=result            outpath=outpath            )if __name__ == '__main__':    app.run(debug=True)